        }
    }


    private async void Editar(int id, string nombre)
    {
        if (await JS.Confirmar("Confirmar", "¿Desea editar la Agencia: " + nombre + "?", IJSExtensions.TipoMensajeSweetAlert.question, "Confirmar Edición!"))
