            NavigationManager.NavigateTo($"agenciaedit/{id}");
        }
    }

    private async void Eliminar(int id, string nombre)
    {
        if (await JS.Confirmar("Confirmar", "¿Está seguro de eliminar la Agencia: " + nombre + "?", IJSExtensions.TipoMensajeSweetAlert.question, "Confirmar Eliminación!"))
